    private void btnCaFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCaFActionPerformed
        // TODO add your handling code here:
        String cadenaCel= txtCel.getText();
        Double c= Double.parseDouble(cadenaCel);
        Double conv= c*(9/5) + 32;
        String farenConv= String.valueOf(conv); 
        lblFaren.setText(farenConv); 
        
    }//GEN-LAST:event_btnCaFActionPerformed

    private void btnFaCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFaCActionPerformed
        // TODO add your handling code here:
        String cadenaFar= txtF.getText();
        Double f= Double.parseDouble(cadenaFar);
        Double conv2= (f-32)*(5/9);
        String celConv=String.valueOf(conv2);
        lblcelConv.setText(celConv); 
        
    }//GEN-LAST:event_btnFaCActionPerformed

